import envexamplesfrom raytracing import *'''DESCRIPTION'''illumination = ImagingPath()illumination.append(Space(d=20))illumination.append(Lens(f=10, diameter=25.4, label="Collector"))illumination.append(Space(d=30))illumination.append(Aperture(diameter=2, label="Field diaphragm"))illumination.append(Space(d=10+30))illumination.append(Lens(f=30, diameter=25.4, label="Condenser"))illumination.append(Space(d=30+30))illumination.append(Lens(f=30, diameter=25.4, label="Objective"))illumination.append(Space(d=30+30))illumination.append(Lens(f=30, diameter=25.4, label="Tube"))illumination.append(Space(d=30+30))illumination.append(Lens(f=30, diameter=25.4, label="Eyepiece"))illumination.append(Space(d=30+2))illumination.append(Lens(f=2, diameter=10, label="Eye Entrance"))illumination.append(Space(d=2))illumination.design(lampRayColors='b')illumination.display(raysList=[                       ObjectRays(diameter=5, halfAngle=0.3, H=3, T=2, z=6.6666666, rayColors='r', color='r'),                       ObjectRays(diameter=2, halfAngle=0.1, H=2, T=2, z=120, rayColors='g', color='g')], removeBlocked=False)illumination2 = ImagingPath()illumination2.append(Space(d=83.333333))illumination2.append(Lens(f=30, diameter=25.4, label="Condenser"))illumination2.append(Space(d=30+30))illumination2.append(Lens(f=30, diameter=25.4, label="Objective"))illumination2.append(Space(d=30+30))illumination2.append(Lens(f=30, diameter=25.4, label="Tube"))illumination2.append(Space(d=30+30))illumination2.append(Lens(f=30, diameter=25.4, label="Eyepiece"))illumination2.append(Space(d=30+2))illumination2.append(Lens(f=2, diameter=10, label="Eye Entrance"))illumination2.append(Space(d=2))illumination2.design(lampRayColors='b')illumination2.display(raysList=[                       ObjectRays(diameter=5, halfAngle=0.1, H=2, T=2, z=0, rayColors='r', color='r'),                       ObjectRays(diameter=2, halfAngle=0.1, H=2, T=2, z=113.333333, rayColors='g', color='g')], removeBlocked=False)